
// chacun séparer un pour l'inscription et un pour la connexion
// (2) #formValidate pour les inscriptions
$("#formValidate").validate({
    rules: {
        pseudo: {
            required: true,
            minlength: 5,
            maxlength: 14
        },
        emails: {
            required: true,
            email: true

        },
        field: {
            required: true,
            minlength: 6
        },
        pass: {
            required: true,
            minlength: 5
        },
        cpassword: {
            required: true,
            minlength: 5,
            equalTo: "#password"
        },
        curl: {
            required: true,
            url: true
        },
        crole: "required",
        ccomment: {
            required: true,
            minlength: 15
        },
        cgender: "required",
        cagree: "required",
    },
    //For custom messages
    messages: {
        pseudo: {
            required: "Enter votre pseudo",
            minlength: "Entrez au moins 5 caractères",
            maxlength: "Pas plus de 15 caractères"
        },
        pass: {
            required: "Enter votre mot de passe",
            minlength: "Entrez au moins 5 caractères"
        },
        emails: {
            required: "Enter votre email",
            erreurmail: "Votre email est incorecte !"
        },
        curl: "Enter your website",
    },
    errorElement : 'div',
    errorPlacement: function(error, element) {
        var placement = $(element).data('error');
        if (placement) {
            $(placement).append(error)
        } else {
            error.insertAfter(element);
        }
    }
});

// fonction du formulaire
// chacun séparer un pour l'inscription un pour la connexion
// (4) Pour les connexions #formValidate.
$(document).ready(function(){
    $('#formValidate').on('input',function () {
        if ($(":input#pseudo").val().length <= 4) {
            $(':input#pseudo').css('border-bottom', 'solid 1px #F44336').css('box-shadow', '0 1px 0 0 #F44336');
        }
        else if ($(":input#pseudo").val().length >= 15) {
            $(':input#pseudo').css('border-bottom', 'solid 1px #F44336').css('box-shadow', '0 1px 0 0 #F44336');
        } else {
            $(':input#pseudo').css('border-bottom', '1px solid #4CAF50').css('box-shadow', '0 1px 0 0 #4CAF50');
        }
    });
});
